import React, { Component } from 'react';
import { Box, Button, Menu, Text, RangeInput } from 'grommet';

class Header extends Component {
	render() {
        let {updateArraySize, updateSortingAlgorithm, arraySize, sort} = this.props;
		return (
			<Box
				background="brand"
				elevation="small"
				direction="row"
				align="center"
			>
				<Box
					direction="row"
                    pad={{ vertical: 'small', left: 'large' }}
                    gap="medium"
					align="center"
					height="72px"
					fill="horizontal"
				>
					<Box>
						<Button label="Generate New Array" />
					</Box>
					<Box direction="row" gap="small" align="center">
						<Text>Size</Text>
						<RangeInput
							value={this.state.arrayRange}
							onChange={updateArraySize}
							min="5"
						/>
					</Box>
					<Box align="center" width="162px">
						<Menu
							label={this.state.sortAlgo}
							items={[
								{
									label: 'Merge Sort',
									onClick: e => {
										this.setState({
											...arguments,
											sortAlgo: e.target.innerText
										});
									}
								},
								{
									label: 'Quick Sort',
									onClick: e => {
										this.setState({
											...arguments,
											sortAlgo: e.target.innerText
										});
									}
								},
								{
									label: 'Heap Sort',
									onClick: e => {
										this.setState({
											...arguments,
											sortAlgo: e.target.innerText
										});
									}
								},
								{
									label: 'Bubble Sort',
									onClick: e => {
										this.setState({
											...arguments,
											sortAlgo: e.target.innerText
										});
									}
								}
							]}
						/>
					</Box>
				</Box>
				<Box width="110px" margin={{right: 'xlarge'}}>
					<Button label="Start" primary />
				</Box>
			</Box>
		);
	}
}

export default Header;
