import React, { Component } from 'react';
import { Box, Chart, Grommet } from 'grommet';
import Header from './Header';
const theme = {
	global: {
		colors: {
			brand: '#061533',
			focus: 'transparent'
		},
		font: {
			family: 'Poppins',
			size: '18px'
		}
	}
};

class App extends Component {
	updateSize = () => {};

	updateSortingAlgorithm = () => {};
	render() {
		return (
			<Grommet theme={theme}>
				<Header
					updateSize={updateSize}
					updateSortingAlgorithm={updateSortingAlgorithm}
				/>
				<Box
					justify="center"
					align="center"
					margin="xlarge"
					height="70vh"
				>
					<Chart
						bounds={[[0, 5], [0, 100]]}
						size="full"
						values={[
							{ value: [5, 60], label: 'sixty' },
							{ value: [4, 80], label: 'eighty' },
							{ value: [3, 40], label: 'forty' },
							{ value: [2, 10], label: 'ten' },
							{ value: [1, 30], label: 'thirty' },
							{ value: [0, 60], label: 'sixty' }
						]}
						aria-label="chart"
					/>
				</Box>
			</Grommet>
		);
	}
}

export default App;
