import React, { Component } from 'react';
import { Box, Button, Text, RangeInput } from 'grommet';

class Header extends Component {
    state = {
        arrayRange: 10
    };
	render() {
		return (
			<Box
				direction="row"
				gap="large"
				pad={{ vertical: 'medium', left: 'large' }}
				background="brand"
				elevation="small"
			>
				<Button>Generate New Array</Button>
				<Box direction="row">
					<Text size="medium">Array size</Text>
                    <RangeInput 
                        value={this.state.arrayRange}
                        onChange={e => {this.setState({arrayRange: e.target.value})}}
                    />
				</Box>
			</Box>
		);
	}
}

export default Header;
