import React, { Component } from 'react';
import { Box, Button, Menu, Text, RangeInput } from 'grommet';

class Header extends Component {
	state = {
		arrayRange: 10,
		sortAlgo: 'Quick Sort'
	};
	render() {
		return (
			<Box
				background="brand"
				elevation="small"
				direction="row"
				align="center"
			>
				<Box
					direction="row"
                    pad={{ vertical: 'small', horizontal: 'large' }}
                    gap="medium"
					align="center"
					height="72px"
					overflow="auto"
					background="brand"
					basis="auto"
					alignSelf="start"
					fill="horizontal"
				>
					<Box>
						<Button label="Generate New Array" />
					</Box>
					<Box direction="row" gap="small" align="center">
						<Text>Size</Text>
						<RangeInput
							value={this.state.arrayRange}
							onChange={e => {
								this.setState({ arrayRange: e.target.value });
							}}
							min="5"
						/>
					</Box>
					
				</Box>
				<Box width="105px" margin={{right: 'large'}}>
					<Button label="Start" primary />
				</Box>
			</Box>
		);
	}
}

export default Header;
