import React, { Component } from 'react';
import { Box, Chart, Grommet } from 'grommet';
import Header from './Header';
const theme = {
	global: {
		colors: {
			brand: '#061533',
			focus: 'transparent'
		},
		font: {
			family: 'Poppins',
			size: '18px'
		}
	}
};

class App extends Component {
	state = {
		arraySize: 10,
		sortingAlgorithm: 'Quick Sort'
	};

	updateArraySize = e => {
		this.setState({ arrayRange: e.target.value });
	};

	updateSortingAlgorithm = e => {
		this.setState({ sortAlgo: e.target.innerText });
	};

	render() {
		return (
			<Grommet theme={theme}>
				<Header
					updateArraySize={this.updateArraySize}
					updateSortingAlgorithm={this.updateSortingAlgorithm}
					arraySize={this.state.arraySize}
					sortingAlgorithm={this.state.sortingAlgorithm}
				/>
				<Box
					justify="center"
					align="center"
					margin="xlarge"
					height="70vh"
				>
					<Chart
						bounds={[[0, 5], [0, 100]]}
						size="full"
						values={[
							{ value: [5, 60], label: 'sixty' },
							{ value: [4, 80], label: 'eighty' },
							{ value: [3, 40], label: 'forty' },
							{ value: [2, 10], label: 'ten' },
							{ value: [1, 30], label: 'thirty' },
							{ value: [0, 60], label: 'sixty' }
						]}
						aria-label="chart"
					/>
				</Box>
			</Grommet>
		);
	}
}

export default App;
