import React, { Component } from 'react';
import { Box, Button, Menu, Text, RangeInput } from 'grommet';

class Header extends Component {
	state = {
		arrayRange: 10,
		sortAlgo: 'Quick Sort'
	};
	render() {
		return (
			<Box
				direction="row"
				gap="xlarge"
				pad={{ vertical: 'small', left: 'xlarge' }}
				background="brand"
				elevation="small"
				align="center"
			>
				<Box>
					<Button>Generate New Array</Button>
				</Box>
				<Box direction="row" gap="small">
					<Text>Size</Text>
					<RangeInput
						value={this.state.arrayRange}
						onChange={e => {
							this.setState({ arrayRange: e.target.value });
						}}
						min="5"
					/>
				</Box>
				<Box>
					<Menu
						label={this.state.sortAlgo}
						items={[
							{
								label: 'Merge Sort',
								onClick: e => {
									this.setState({...arguments, sortAlgo: e.target.innerText});
								}
							},
							{
								label: 'Quick Sort',
								onClick: e => {
									this.setState({...arguments, sortAlgo: e.target.innerText});
								}
                            },
                            {
								label: 'Heap Sort',
								onClick: e => {
									this.setState({...arguments, sortAlgo: e.target.innerText});
								}
                            },
                            {
								label: 'Bubble Sort',
								onClick: e => {
									this.setState({...arguments, sortAlgo: e.target.innerText});
								}
							}
						]}
					/>
				</Box>
                <Box>
                    <Button primary  pad="30px">Start</Button>
                </Box>
			</Box>
		);
	}
}

export default Header;
