import React, { Component } from 'react';
import { Box, Button, Paragraph, RangeInput } from 'grommet';

class Header extends Component {
	state = {
		arrayRange: 10
	};
	render() {
		return (
			<Box
				direction="row"
				gap="xlarge"
				pad={{ vertical: 'medium', left: 'xlarge' }}
				background="brand"
				elevation="small"
			>
                <Box direction="row">
                <Button>Generate New Array</Button>
                </Box>
                <Box direction="row">
                <Paragraph margin="0" fill="true">Array size</Paragraph>
                <RangeInput
					value={this.state.arrayRange}
					onChange={e => {
                        this.setState({ arrayRange: e.target.value });             
                    }}
                    min="5"
				/>
                </Box>
			</Box>
		);
	}
}

export default Header;
