import React, { Component } from 'react';
import { Box, Button, Menu, Text, RangeInput } from 'grommet';

class Header extends Component {
	state = {
        arrayRange: 10,
        sortAlgo: "Bubble Sort"
	};
	render() {
		return (
			<Box
				direction="row"
				gap="xlarge"
				pad={{ vertical: 'small', left: 'xlarge' }}
				background="brand"
                elevation="small"
                align="center"
			>
				<Box>
					<Button>Generate New Array</Button>
				</Box>
				<Box direction="row" gap="small">
					<Text>Size</Text>
					<RangeInput
						value={this.state.arrayRange}
						onChange={e => {
							this.setState({ arrayRange: e.target.value });
						}}
						min="5"
					/>
				</Box>
				<Box>
					<Menu
                        label={this.state.sortAlgo}
						items={[
							{
								label: 'First Action',
								onClick: e => {
                                    this.setState(e.target.value);
                                    console.log(e.target.innerH);
                                    
								}
							},
							{ label: 'Second Action', onClick: e => {} }
						]}
					/>
				</Box>
			</Box>
		);
	}
}

export default Header;
