import React, { Component } from 'react';
import { Box, Button, Menu, Text, RangeInput } from 'grommet';

class Header extends Component {
	state = {
		arrayRange: 10,
		sortAlgo: 'Quick Sort'
	};
	render() {
		return (
			<Box background="brand" elevation="small">
				<Box
					direction="row"
					pad={{ vertical: 'small', horizontal: 'small' }}
					align="center"
					justify="evenly"
					width="1000px"
                    height="72px"
                    overflow="hidden"
                    basis
				>
					<Box>
						<Button label="Generate New Array"/>
					</Box>
					<Box direction="row" gap="small" align="center">
						<Text>Size</Text>
						<RangeInput
							value={this.state.arrayRange}
							onChange={e => {
								this.setState({ arrayRange: e.target.value });
							}}
							min="5"
						/>
					</Box>
					<Box align="center" width="162px">
						<Menu
							label={this.state.sortAlgo}
							items={[
								{
									label: 'Merge Sort',
									onClick: e => {
										this.setState({
											...arguments,
											sortAlgo: e.target.innerText
										});
									}
								},
								{
									label: 'Quick Sort',
									onClick: e => {
										this.setState({
											...arguments,
											sortAlgo: e.target.innerText
										});
									}
								},
								{
									label: 'Heap Sort',
									onClick: e => {
										this.setState({
											...arguments,
											sortAlgo: e.target.innerText
										});
									}
								},
								{
									label: 'Bubble Sort',
									onClick: e => {
										this.setState({
											...arguments,
											sortAlgo: e.target.innerText
										});
									}
								}
							]}
						/>
					</Box>
					<Box>
						<Button label="Start" primary />
					</Box>
				</Box>
			</Box>
		);
	}
}

export default Header;
